package test;

public class BitwiseOperatorTest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a1=16;
		int a2 =15;
		System.out.println(a1 <<1);
		System.out.println(a1 >>1);
		System.out.println(a2 <<1);
		System.out.println(a2 >>1);
		System.out.println(a2 >>>1);
	}

}
